import React, { useState } from "react";
import {
  Button,
  ButtonGroup,
  Typography,
  Container,
  TextField,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormLabel,
  FormControl,
} from "@mui/material";
import StarBorderPurple500Icon from "@mui/icons-material/StarBorderPurple500";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import ModeNightOutlinedIcon from "@mui/icons-material/ModeNightOutlined";
import { blue, green, purple, yellow } from "@mui/material/colors";
import makeStyles from "@mui/styles/makeStyles";

const useStyles = makeStyles({
  btn: {
    fontSize: 20,
    background: "red",
    "&:hover": {
      background: "blue",
    },
  },
  title: {
    textDecoration: "underline",
    marginBottom: 20,
  },
  fields: {
    background: "secondary",
    margin: 50,
  },
});

export default function Notes() {
  const classes = useStyles();

  const [title, setTitle] = useState("");
  const [details, setDetails] = useState("");
  const [titleError, setTitleError] = useState(false);
  const [detailsError, setDetailsError] = useState(false);
  const [category, setCategory] = useState("car");
  const handleSubmit = (e) => {
    e.preventDefault();
    setDetailsError(false);
    setTitleError(false);

    if (title == "") {
      setTitleError(true);
    }
    if (details == "") {
      setDetailsError(true);
    }

    if (title && details) {
      console.log(title, details, category);
    }
  };

  return (
    <Container>
      {/* <Typography variant="h4" color="primary" align="center">
        Notes page
      </Typography> */}
      <Typography noWrap variant="h4" color="secondary" align="center">
        printing and typesetting industry. Lorem Ipsum has been the industry's
        standard dummy text ever since the Ipsum , when an unknown printer to
      </Typography>
      <Typography
        variant="h2"
        component="h1"
        color="textSecondary"
        align="center"
        gutterBottom
        className={classes.title}
      >
        Notes page
      </Typography>
      <Button type="submit" color="primary">
        Submit
      </Button>
      <Button type="submit" color="primary" variant="outlined">
        Submit22
      </Button>
      <ButtonGroup color="secondary">
        <Button className={classes.btn}>Submit</Button>
        <Button color="secondary">Submit</Button>
        <Button>Submit</Button>
      </ButtonGroup>

      <br />
      <StarBorderPurple500Icon color="secondary" fontSize="large" />
      <StarBorderPurple500Icon color="action" fontSize="small" />

      <form noValidate autoComplete="off" onSubmit={handleSubmit}>
        <TextField
          onChange={(e) => setTitle(e.target.value)}
          className={classes.fields}
          variant="outlined"
          label="Name family"
          fullWidth
          error={titleError}
        />
        <TextField
          onChange={(e) => setDetails(e.target.value)}
          className={classes.fields}
          variant="outlined"
          label="Details"
          color="secondary"
          fullWidth
          required
          multiline
          rows={4}
          error={detailsError}
        />

        <FormControl className={classes.fields}>
          <FormLabel>Note Category</FormLabel>

          <RadioGroup
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <FormControlLabel value="Money" control={<Radio />} lable="Money" />
            <FormControlLabel value="car" control={<Radio />} lable="car" />
            <FormControlLabel value="home" control={<Radio />} lable="home" />
            <Radio value="hello" />
            <Radio value="goodbye" />
          </RadioGroup>
        </FormControl>

        <Button
          type="submit"
          color="secondary"
          variant="contained"
          startIcon={<KeyboardArrowRightIcon />}
          endIcon={<ModeNightOutlinedIcon />}
        >
          Submit me
        </Button>
      </form>
    </Container>
  );
}
